mapper.insert:
insert into t_health_medication (medication_id, user_id, drug_id,
medication_date, medication_type, is_take
)
values (#{medicationId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{drugId,jdbcType=BIGINT},
#{medicationDate,jdbcType=DATE}, #{medicationType,jdbcType=CHAR}, #{isTake,jdbcType=BIT}
)

mapper.select:
select

from t_health_medication
where user_id = #{userId,jdbcType=BIGINT}

  and medication_date between #{start,jdbcType=DATE} and #{end,jdbcType=DATE}


  and medication_type = #{type,jdbcType=CHAR}


  and drug_id = #{drugId,jdbcType=BIGINT}

mapper.delete:
delete from t_health_medication
where user_id = #{userId,jdbcType=BIGINT}

  and medication_date between #{start,jdbcType=DATE} and #{end,jdbcType=DATE}


  and medication_type = #{type,jdbcType=CHAR}
